struct buffer      *buffers;
struct v4l2_buffer  buf;
int                 n_buffers;

static void xioctl(int fh, int request, void *arg)
{
  int r;
  do {
    r = v4l2_ioctl(fh, request, arg);
  } while (r == -1 && ((errno == EINTR) || (errno == EAGAIN)));
  if (r == -1) {
    fprintf(stderr, "error %d, %s\n", errno, strerror(errno));
    exit(EXIT_FAILURE);
  }
}

