    if (!paused)
    {
      // Business Logic for captureImage_server Server providing captureImage Service
      fd_set                          fds;
      struct timeval                  tv;
      enum v4l2_buf_type              type;
      int                             r;

      for (int i = 0; i < n_buffers; ++i) {
	CLEAR(buf);
	buf.type = V4L2_BUF_TYPE_VIDEO_CAPTURE;
	buf.memory = V4L2_MEMORY_MMAP;
	buf.index = i;
	xioctl(videoFD, VIDIOC_QBUF, &buf);
      }
      type = V4L2_BUF_TYPE_VIDEO_CAPTURE;

      xioctl(videoFD, VIDIOC_STREAMON, &type);
      do {
	FD_ZERO(&fds);
	FD_SET(videoFD, &fds);

	/* Timeout. */
	tv.tv_sec = 2;
	tv.tv_usec = 0;

	r = select(videoFD + 1, &fds, NULL, NULL, &tv);
      } while ((r == -1 && (errno = EINTR)));
      if (r == -1) {
	perror("select");
	return false;
      }

      CLEAR(buf);
      buf.type = V4L2_BUF_TYPE_VIDEO_CAPTURE;
      buf.memory = V4L2_MEMORY_MMAP;
      xioctl(videoFD, VIDIOC_DQBUF, &buf);

      //fwrite(buffers[buf.index].start, buf.bytesused, 1, fout);

      type = V4L2_BUF_TYPE_VIDEO_CAPTURE;
      xioctl(videoFD, VIDIOC_STREAMOFF, &type);

      res.imgVector.reserve(buf.bytesused);
      std::copy(&((unsigned char *)buffers[buf.index].start)[0],
		&((unsigned char *)buffers[buf.index].start)[0] + buf.bytesused,
		back_inserter(res.imgVector));
      res.width = width;
      res.height = height;

      return true;
    }
  return false;

  if (!paused)
    {
      // read current value for radial position (encoder)
      radialCurrent = radialMotoreQEP.get_position();
      //ROS_INFO("Raidal Actuator Encoder Reading: %d",radialCurrent);

      unsigned int limitSwitchState = 0;
      unsigned int backwardPinState = 0;
      gpio_get_value(lowerLimitSwitchPin,&limitSwitchState);
      gpio_get_value(motorBackwardPin,&backwardPinState);
      if (backwardPinState && !limitSwitchState)
